    public function getSourceContext()
    {
        return new Source("{% extends 'base.html.twig' %}

{% block title %}Koababi | Connexion{% endblock %}

{% block body %}
<div class=\"block-small container-form\">
    <form method=\"post\">
        {% for message in app.flashes('success') %}
            <div class=\"alert alert-success\">
                {{ message }}
            </div>
        {% endfor %}
        
        {% if error %}
            <div class=\"alert alert-danger\">{{ error.messageKey|trans(error.messageData, 'security') }}</div>
        {% endif %}

        {% if app.user %}
            <div class=\"mb-3\">
                Vous êtes connecté en tant que {{ app.user.username }}, <a href=\"{{ path('app_logout') }}\">Se déconnecter</a>
            </div>
        {% endif %}

        <h1 class=\"h3 mb-3 font-weight-normal\">Merci de vous identifier</h1>
        <label for=\"inputEmail\">Email</label>
        <input type=\"email\" value=\"{{ last_username }}\" name=\"email\" id=\"inputEmail\" class=\"form-control\" autocomplete=\"email\" required autofocus>
        <label for=\"inputPassword\">Mot de passe</label>
        <input type=\"password\" name=\"password\" id=\"inputPassword\" class=\"form-control\" autocomplete=\"current-password\" required>

        <input type=\"hidden\" name=\"_csrf_token\" value=\"{{ csrf_token('authenticate') }}\">

        {#
            Uncomment this section and add a remember_me option below your firewall to activate remember me functionality.
            See https://symfony.com/doc/current/security/remember_me.html

            <div class=\"checkbox mb-3\">
                <label>
                    <input type=\"checkbox\" name=\"_remember_me\"> Remember me
                </label>
            </div>
        #}
        <div class=\"text-center\">
            <button class=\"btn form-button mt-4\" type=\"submit\">Connexion</button>
        </div>
        
    </form>
</div>
<div class=\"text-center m-4\">
    <a href=\"{{path('register')}}\" class=\"\">Créer un compte</a> | <a href=\"{{path('reset_password')}}\" class=\"\">Mot de passe oublié ?</a>
</div>
    
{% endblock %}
", "security/login.html.twig", "/Applications/MAMP/htdocs/KOABABI/Koababi/templates/security/login.html.twig");
    }
